@import url('https://fonts.cdnfonts.com/css/latin-modern-roman');
@import url('https://fonts.googleapis.com/css2?family=Saira&display=swap');


.container * {
 outline: none !important;
}                

.container {
  display: flex;
  justify-content: start !important;
}

.Todo * {
 transition: all .3s ease-out; 
}

.Todo::before {
  content: 'Todo List';
display:block;
font-variant: small-caps;
text-shadow: 0 1px 1px white;
font-size: 20px;
text-align: left;
margin-bottom: 1vw;
margin-left: 3px
font-family: 'LMRoman10', serif;
}

.Todo::after {
content: '';
border: 1px solid ;
width: 100px;
position: fixed;
left: calc(200px - 182px);
top: 50px;
border-image: linear-gradient(to right, #8fbcbb, #88c0d0, #81a1c1, #5e81ac) 1;
}


.Todo {
background: #81a1c11b;
backdrop-filter: blur(1px);
padding: 1vw;
font-size: 15px;
font-family: 'Saira', sans;
border-radius: .4vw;
border: 2px solid #4c566a;
width: 250px;
box-shadow: 0 0 2px #88c0d0, inset 0 0 2px #4c566a;
}

.TodoItem {
max-width: 200px;
display: flex;
padding: .3vw;
background: #4c566aaa;
border-radius: .23vw;
margin: .3vw;
width: 200px;
justify-content: end !important;
align-content: center;
text-overflow: ellipsis;
overflow-x:hidden;
}

a[class="complete"] {
color: #8fbcbb;
margin-right: 8px;
display: inline-block;
}

a[class="delete"] {
color: #bf616a;
display:inline-block;
margin-right: 8px;
}

span[contenteditable="true"] {

}

/*.Todo i > svg line {
color: #77fdaa;
transform: scale(1.12)
}*/

.Todo i > svg circle {
transform: scale(1.08);
color: #8fbcbb;
}


.Slot:has(.Todo)::before {
 width: 60px;
height: 60px;
background: url("https://cdn-icons-png.freepik.com/256/221/221406.png?semt=ais_hybrid") center / 40px no-repeat;
content: '';
display: block;
position: absolute;
filter: invert(1);
z-index: 5;
right: 20px;
top: 20px;
}
